const sowTemplate = {
  "softwareDevelopment": {
    "scope": "This scope of work outlines the project requirements, deliverables, and timelines for the software development project. It includes all the necessary information for project execution and quality assurance. The service provider will develop, test, and deploy software according to the requirements",
     "clauses":["Requirement Analysis", "System Design", "Coding and Development", "Testing and QA", "Deployment", "Maintenance"]
  },
  "consultingServices": {
    "scope": "This scope of work is for the provision of consulting services and includes the terms, timelines, and required deliverables. The consultant will provide advice and strategies on project management and business operations.",
     "clauses":["Project Planning", "Strategic Advice", "Process Optimization", "Data Analysis", "Report Preparation"]
  },
    "marketingServices": {
    "scope": "This scope of work is for the delivery of marketing services aimed at achieving business objectives.  The marketing agency will create and implement marketing campaigns across various platforms, measure the results, and optimize.",
     "clauses":["Market Analysis", "Campaign Creation", "Content Development", "Social Media Management", "Performance Analysis"]
  }
};

const serviceCategory = $json["serviceCategory"];
const selectedTemplate = sowTemplate[serviceCategory];

if(selectedTemplate){
  return  [{"json":{"scope":selectedTemplate.scope,"clauses":selectedTemplate.clauses}}]
}

return []

